clear; clc; close all;addpath(genpath('./3Dtools'));% input your data directorymesh_dir='../data/cover/PSBn';% input your save_pathsave_pth = './';mesh = dir(mesh_dir);mesh_num=size(mesh)-2;k=10^(-6);% allowed vertex change rangeI=(0:15)-7;parpool(32);for i=1:mesh_num    name = mesh(i+2).name;    b=strsplit(name,'.');    cost1(b{1},fullfile(mesh_dir,name),k,I,save_pth);endpoolobj = gcp('nocreate');delete(poolobj);